import React, { useEffect, useState } from 'react';
import './box.css';

export default function App() {

    const [color, changeColor] = useState(false);
    function checkColor(state){
      if (state) {
        return "blackBox"
      }
      return "whiteBox"

    }
    if (changeColor()) {
      <div className='blackBox'></div>
    }

    return (
    // <div className="container">
    //     {/* <button onClick={() => changeColor} <div className="aBox"></div> */}

    // </div>);
    <div className="container">
        <button className={checkColor(color)} onClick={() => 
          changeColor((prevColor) => !prevColor)}>

           {color.toString()}
           </button>

        <div ></div>
    </div>
    )
}
