import React, {useState} from 'react';
import Square from './Square';

function Grid() {
    const [grid, setGrid] = useState([
        [0,0],
        [0,0]
    ])




    const handleClick = (x, y) => {
        let newGrid = []
        for (let i = 0; i < grid.length; i++) {
            let row = [];
            for (let j = 0; j < grid.length; j++) {
                if (i == x && j == y) {
                    if(grid[x][y] == 0) row.push(1)
                    else {
                        row.push(0)
                    }
                } else {
                    row.push(grid[i][j])
                }
            }
            newGrid.push(row)
        }
        setGrid(newGrid)
    }

    const [count, setCount] = useState(0)

    function handleCount(val) {
        if (val === 1) {
            setCount(count + 1)
        } else {
            setCount(count - 1)
        }
    }

    const renderSquare = (x, y) => {
        return <Square count={handleCount(grid[x][y])}val={grid[x][y]} x={x} y={y} click={(a,b) => handleClick(a,b)}>

        </Square>
    }

    return (
        <div className="grid">
            <div className="count">Count: {count}</div>
            <button>{renderSquare(0,0)}</button>
            <button>{renderSquare(0,1)}</button>
            <button>{renderSquare(1,0)}</button>
            <button>{renderSquare(1,1)}</button>
        </div>
    )
}

export default Grid;
